  };

  return (
    <div className="App">
      <form className="column"
        onSubmit={handleSubmit}>
        <div className="row">
          <p>YouTube link:</p>
          <input 
            type="text" 
            name="link" 
            className="input"
            // onChange={handleChange}
            />
        </div>
        <div className="row">
          <p>Resolution:</p>
          <input type="radio" value="720p">720p (HD)</input>
          <input type="radio" value="1080p">1080p (FHD)</input>
          <input type="radio" value="2160p">2160p (4K)</input>
          <input type="radio" value="4320p">4320p (8K)</input>
        <button name="submit" className="button">Download</button>
        </div>
        <p>{select}</p>
      </form>
      <div className="list">
      </div>
    </div>
  );
}

export default App;
